import { IonicModule } from '@ionic/angular';
// src/app/home/home.page.ts
import { Component, CUSTOM_ELEMENTS_SCHEMA, ElementRef, OnInit, ViewChild, inject } from '@angular/core';
import { IonicSlides } from '@ionic/angular'; // pour le comportement natif Ionic (optionnel)
import { CommonModule } from '@angular/common';
import { Destination, Offer } from 'src/app/interfaces/travel.models';
import { HomeService } from 'src/app/services/home-service';
import { addIcons } from 'ionicons';
import { libraryOutline, sunnyOutline, trailSignOutline } from 'ionicons/icons';
import { DestinationCardComponent } from 'src/app/components/destination-card/destination-card.component';
import { OfferCardHorizontalComponent } from 'src/app/components/offer-card-horizontal/offer-card-horizontal.component';

@Component({
  selector: 'app-home',
  templateUrl: './home.page.html',
  styleUrls: ['./home.page.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule, DestinationCardComponent, OfferCardHorizontalComponent],
  schemas: [CUSTOM_ELEMENTS_SCHEMA],
})
export class HomePage implements OnInit {
  private homeService = inject(HomeService);

  @ViewChild('swiper', { static: false }) swiperRef?: ElementRef;

  // Ajoute IonicSlides si tu veux conserver le réglage natif (conseillé pour ressentir comme ion-slides)
  swiperModules = [IonicSlides];
  loading = false;

  featuredDestinations: Destination[] = [
    {
      id: 1,
      name: 'Maldives',
      city: null,
      description: 'Tropical paradise with stunning beaches.',
      images: ['data:image/jpeg;base64,',
        'data:image/jpeg;base64,'],
      latitude: 3.2028,
      longitude: 73.2207,
      category: [{ id: 1, name: 'Beach', verbose_name: 'Plage' }],
    },
    {
      id: 2,
      name: 'Swiss Alps',
      city: null,
      description: 'Breathtaking mountain range for hiking and skiing.',
      images: ['data:image/jpeg;base64,',
        'data:image/jpeg;base64,'
      ],
      latitude: 46.8,
      longitude: 8.2,
      category: [{ id: 2, name: 'Mountain', verbose_name: 'Montagne' }],
    },
  ];

  specialOffers: Offer[] = [
    {
      id: 1,
      destination: this.featuredDestinations[0],
      title: 'Luxury Villa in Maldives',
      description: '7-night stay in a water villa with private pool.',
      price: 3000000,
      start_date: '2025-10-01',
      end_date: '2025-10-31',
      available_slots: 10,
      created_by: null,
      cancellation_policy: 'Flexible',
    },
    {
      id: 2,
      destination: this.featuredDestinations[1],
      title: 'Ski Adventure in the Alps',
      description: 'Full-week ski pass and accommodation in a cozy chalet.',
      price: 150000,
      start_date: '2026-01-15',
      end_date: '2026-03-15',
      available_slots: 20,
      created_by: null,
      cancellation_policy: 'Moderate',
    },
  ];

  popularDestinations: Destination[] = [...this.featuredDestinations].reverse();


  ngOnInit() {
    // this.loadData();
    addIcons({
      trailSignOutline,
      sunnyOutline,
      libraryOutline
    })
  }
  onSlideChange(e: any) {
    // e.target est le DOM element swiper-container
    const active = this.swiperRef?.nativeElement.swiper?.activeIndex;
    console.log('slide changed, activeIndex=', active);
  }

  // Exemple: accéder à l'instance Swiper
  goToNext() {
    this.swiperRef?.nativeElement.swiper?.slideNext();
  }

  loadData() {
    this.loading = true;

    this.homeService.getFeaturedDestinations().subscribe({
      next: (data) => (this.featuredDestinations = data),
      error: (err) => console.error('Erreur destinations phares', err),
    });

    this.homeService.getSpecialOffers().subscribe({
      next: (data) => (this.specialOffers = data),
      error: (err) => console.error('Erreur offres spéciales', err),
    });

    this.homeService.getPopularDestinations().subscribe({
      next: (data) => {
        this.popularDestinations = data;
        this.loading = false;
      },
      error: (err) => {
        console.error('Erreur destinations populaires', err);
        this.loading = false;
      },
    });
  }

}
